        	  , USER_COMPANY
        	  , CREATOR
        	  , CREATE_TIME
        	  , MODIFIER
        	  , MODIFIED_TIME
			)
		VALUES
			(
				#{userId}
			  , #{userPw}
			  , #{userName}
			  , #{userPhone1}
			  , #{userPhone2}
			  , #{userPhone3}
			  , #{userAddr1}
			  , #{userAddr2}
			  , #{userCompany}
			  , 'SYSTEM'
			  , TO_CHAR(SYSDATE)
			  , 'SYSTEM'
			  , TO_CHAR(SYSDATE)
			)

mapper.select:
	SELECT USER_ID
      FROM USER_INFO
     WHERE USER_ID = #{userId}

	SELECT USER_ID
      FROM USER_INFO
     WHERE USER_ID = #{userId}
       AND USER_PW = #{userPw}

	SELECT COUNT(*)
      FROM USER_INFO
     WHERE USER_ID = #{userId}
       AND USER_PW = #{userPw}

    	SELECT COUNT(*) AS TOTAL_CNT
		  FROM POST_FILE_TABLE, BOARD	
		 WHERE POST_FILE_TABLE.BOARD_NUM = BOARD.BOARD_NUM
		   AND BOARD.BOARD_TYPE = #{fileGroup }
           AND BOARD.BOARD_NUM  = #{boardNum }

mapper.insert:
INSERT INTO BOARD ( BOARD_TYPE , BOARD_NUM , BOARD_TITLE , BOARD_COMMENT , CREATOR , CREATE_TIME , MODIFIER , MODIFIED_TIME , BOARD_HITS , BOARD_REF , BOARD_STEP , BOARD_LEV , FILE_ROOT , FILE_SIZE ) SELECT a.* FROM (
SELECT #{item.boardType} as boardType , (SELECT NVL(TO_NUMBER(MAX(BOARD_NUM)+(#{index }+1) ),1) FROM BOARD WHERE BOARD_TYPE = #{item.boardType }) as boardNum , #{item.boardTitle } as boardTitle , #{item.boardComment } as boardComment , #{item.creator } as creator , TO_CHAR(SYSDATE,'YYYY-MM-DD HH24:MI') as createTime , 'SYSTEM' as modifier , TO_CHAR(SYSDATE,'YYYY-MM-DD HH24:MI') as modifiedTime , '0' as boardHits , (SELECT NVL(TO_NUMBER(MAX(BOARD_NUM)+(#{index }+1) ),1) FROM BOARD WHERE BOARD_TYPE = #{item.boardType }) as boardRef , '0' as boardStep , '0' as boardLev , #{item.fileRoot, jdbcType=VARCHAR } , #{item.fileSize, jdbcType=INTEGER } FROM DUAL
) a

mapper.update:
    	UPDATE BOARD 
    	   SET BOARD_TITLE = #{boardTitle }
	         , BOARD_COMMENT = #{boardComment }
    	 WHERE BOARD_TYPE = #{boardType }
    	   AND BOARD_NUM  = #{boardNum }

mapper.delete:
DELETE
  FROM BOARD 
 WHERE BOARD_TYPE = #{boardType }
   AND BOARD_NUM  = #{boardNum }

mapper.select:
SELECT CODE_ID , CODE_NAME , BOARD_TYPE , BOARD_NUM , BOARD_TITLE , BOARD_COMMENT , TOTAL_CNT , BOARD_HITS , CREATE_TIME , BOARD_REF , BOARD_STEP , BOARD_LEV , CREATOR FROM ( SELECT CODE_ID , CODE_NAME , BOARD_TYPE , BOARD_NUM , BOARD_TITLE , BOARD_COMMENT , ROW_NUMBER() OVER(ORDER BY BOARD_NUM DESC) AS NUMROW , COUNT(*) OVER() AS TOTAL_CNT , BOARD_HITS , BOARD.CREATE_TIME , BOARD_REF , BOARD_STEP , BOARD_LEV , BOARD.CREATOR FROM BOARD JOIN COM_CODE ON BOARD.BOARD_TYPE = COM_CODE.CODE_ID
WHERE BOARD_TYPE IN
#{item}
) WHERE NUMROW BETWEEN (#{pageNo}-1)*10 + 1 AND (#{pageNo})*10 ORDER BY BOARD_REF DESC, BOARD_STEP ASC

mapper.update:
UPDATE BOARD
   SET BOARD_HITS = BOARD_HITS+1
 WHERE BOARD_NUM = #{boardNum}
   AND BOARD_TYPE = #{boardType}

UPDATE BOARD
   SET BOARD_STEP = BOARD_STEP + 1
 WHERE BOARD_REF  = #{boardRef}
   AND BOARD_STEP > #{boardStep}

mapper.insert:
    INSERT INTO 
    POST_FILE_TABLE
    	(
    		FILE_ID
	      , FILE_WRITER
	      , FILE_NUM
	      , FILE_GROUP
	      , FILE_ORG_NAME
	      , FILE_SAVE_NAME
	      , FILE_TYPE
	      , FILE_SIZE
	      , FILE_PATH
	      , FILE_REG_DTTM
	      , BOARD_NUM
	    )
	VALUES
		(
			(SELECT NVL(TO_NUMBER(MAX(FILE_ID)+1),1) FROM POST_FILE_TABLE)
	      , #{fileWriter }
	      ,(SELECT NVL(TO_NUMBER(MAX(FILE_NUM)+1),1) FROM POST_FILE_TABLE)
	      , #{fileGroup }
	      , #{fileOrgName }
	      , #{fileSaveName }
	      , #{fileType }
	      , #{fileSize }
	      , #{filePath }
	      , TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS')
	      , (SELECT MAX(BOARD_NUM) FROM BOARD WHERE BOARD_TYPE= #{fileGroup })
	    )

INSERT INTO POST_FILE_TABLE ( FILE_ID , FILE_WRITER , FILE_NUM , FILE_GROUP , FILE_ORG_NAME , FILE_SAVE_NAME , FILE_TYPE , FILE_SIZE , FILE_PATH , FILE_REG_DTTM , BOARD_NUM ) SELECT a.* FROM (
SELECT (SELECT NVL(TO_NUMBER(MAX(FILE_ID)+(#{index }+1)),1) FROM POST_FILE_TABLE) as fileId , #{item.fileWriter, jdbcType=VARCHAR } as fileWriter , (SELECT NVL(TO_NUMBER(MAX(FILE_NUM)+(#{index }+1)),1) FROM POST_FILE_TABLE) as fileNum , #{item.fileGroup, jdbcType=VARCHAR } as fileGroup , #{item.fileOrgName, jdbcType=VARCHAR } as fileOrgName , #{item.fileSaveName, jdbcType=VARCHAR } as fileSaveName , #{item.fileType, jdbcType=VARCHAR } as fileType , #{item.fileSize, jdbcType=INTEGER } as fileSize , #{item.filePath, jdbcType=VARCHAR } as filePath , TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS') as fileRegDttm , (SELECT MAX(BOARD_NUM) FROM BOARD WHERE BOARD_TYPE= #{item.fileGroup, jdbcType=VARCHAR }) as boardNum FROM DUAL
) a

